// You can test the distance field by moving the mouse around
float getDistance(in vec2 p) {
    return texture(iChannel0, p / iResolution.xy).x;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = texture(iChannel1, fragCoord / iResolution.xy * vec2(0.5, 2.0));
    fragColor.rgb -= getDistance(fragCoord) / 50.0;

    fragColor.rgb += (0.25 + 0.25 * sin(getDistance(fragCoord))) * (1.0 - fragColor.a);

    float bcircle = abs(length(fragCoord - iMouse.xy) - getDistance(iMouse.xy)) - 1.0;
    fragColor.rgb = mix(fragColor.rgb, vec3(0.0, 0.0, 1.0), smoothstep(1.0, 0.0, bcircle));
}

/*
The idea is that each cell carries a piece of distance information that gets passed
along from cell to cell. The outline of the algorithm is extremely simple:
1. If the current cell is filled, ignore it but retain its distance.
2. If any neighboring cell is filled, update the current cell's distance to the
   minimum of its distance and the distance of the neighboring cell plus the
   distance between both cells.
*/

#define textureLoaded(s) (textureSize(s, 0).x > 1) // https://www.shadertoy.com/view/wldGW8
#define bufferResized() ivec2(iResolution.xy) != ivec2(texelFetch(iChannel1, ivec2(0), 0).xy)
#define SQRT_2 sqrt(2.0)

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = vec4(0.0, 0.0, 0.0, 0.0);
    ivec2 iFragCoord = ivec2(fragCoord);
    if (iFrame > 0 && textureLoaded(iChannel1)) {
        fragColor = texelFetch(iChannel0, iFragCoord, 0);

        // Only update if the current cell has not been filled
        if (fragColor.y < 0.5) {
            float closest;
            bool foundCandid = false; // Flags whether a first candidate for the shortest distance has been found

            // Loop over neighboring cells
            for (int i=-1; i < 2; i++) {
                for (int j=-1; j < 2; j++) {

                    // Ignore the center (current) cell
                    if (!(i == 0 && j == 0)) {
                        vec2 cell = texelFetch(iChannel0, iFragCoord + ivec2(i, j), 0).xy;
                        if (cell.y > 0.5) {
                            // The distance is 1 if the cell is on an edge and sqrt(2) if it is on a corner
                            float dist = (i == 0 || j == 0) ? cell.x + 1.0 : cell.x + SQRT_2;
                            closest = !foundCandid ? dist : min(closest, dist);

                            // Cells on the edges are always the closest so we might be able to break out early
                            if (i == 0 || j == 0) break;

                            foundCandid = true;
                        }

                        // Update filled status of the current cell
                        fragColor.y = max(fragColor.y, cell.y);
                    }
                }
            }

            // Store closest distance if one has been found
            if (foundCandid) fragColor.x = closest;
        }
    }

    if (iFrame == 0 || bufferResized() || !textureLoaded(iChannel1)) {
        // Scale texture and read from the alpha (opacity) channel
        fragColor.x = 0.0;
        fragColor.y = round(texture(iChannel2, fragCoord / iResolution.xy * vec2(0.5, 2.0)).w);
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = vec4(iChannelResolution[0].xy, 0.0, 1.0);
}
