// 135 byte version here https://www.dwitter.net/d/20144
// Converting old glslsandbox code - http://glslsandbox.com/e#68778.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = ( fragCoord.xy / iResolution.xy );
	float X = ceil(p.x*60.);
	float Y = ceil(p.y*38.);
	float t = iTime*0.7;
	float o = sin(X*Y+Y/2.*Y/9.*(sin(t/9.)))/pow(128.,cos(Y/9.*cos(t)+X/11.*sin(t)));
	fragColor = vec4( vec3( o, o*.07, 0.), 1. );

}
