void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;

    st -= 0.5;
    st.x *= iResolution.x/iResolution.y;

    float d = length(st);
    float c;
     c=smoothstep(0.1*sin(iTime),0.1*cos(iTime)+tan(iTime)*sin(iTime) ,d );

    vec3 color =vec3(c*sin(iTime),c*cos(iTime),c*tan(iTime))-fract(sin(dot(st.xy, vec2(12.9898, 78.233))) * 43758.5453)/tan(iTime);

    // Output to screen
    fragColor = vec4(color,1.0);
}
