void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy/iResolution.xy;


    float radiai = 0.0015;


    vec2 Metaball = vec2(0.5,0.5);
    vec2 Metaball2 = vec2(0.5+sin(iTime)/3.0,0.5);

    float UVtoMB = (uv.x-Metaball.x)*-(uv.x-Metaball.x)+(uv.y-Metaball.y)*(uv.y-Metaball.y);
    float UVtoMB2 = (uv.x-Metaball2.x)*(uv.x-Metaball2.x)+(uv.y-Metaball2.y)*-(uv.y-Metaball2.y);

    float finalMB = radiai/UVtoMB;


    finalMB += radiai/UVtoMB2;






    vec3 col = vec3(1.0,1.0,(sin(iTime)/2.0)+0.5);

    if(finalMB >= 0.18){
    	col *= vec3(uv.x,uv.y,1.0);

    }
    else {
    	col *= 0.0;
    }
    // Output to screen
    fragColor = vec4(mix(vec3(1.0,0.3,0.5),col,finalMB+0.7),1.0);
}
