float n21 (vec2 p) {
    return fract(sin(p.x*124.43 + p.y*5432.)*44433.);
}

vec3 getColorByIndex(float id) {
    float n = n21(vec2(id));
    vec3 color = vec3(sin(n+iTime), fract(n*10.23), fract(n*453.223));
    return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoords) {
    vec2 uv = fragCoords / iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;

    float circles = 8.;
    float swirl = 2.;

    float a = atan(uv.x, uv.y) / 6.28 + .5;

    float len = length(uv);

    float d = len + iTime*0.1 + a/circles * swirl;

    float nd = d * circles;

    float id = floor(nd);
    float df = fract(nd);

    vec3 color = getColorByIndex(id);
    vec3 color1 = getColorByIndex(id + swirl);

    color = mix(color, color1, 1. - a);

    fragColor = vec4(color, 1.);

}
