float random (in vec2 _st) {
    return fract(sin(dot(_st.xy,
                         vec2(12.9398,78.333)))*
        43783.5453123);
}



vec3  line( in vec2 p, in vec2 a, in vec2 b,in float t,in vec3 c )
{
    vec2 pa = p - a;
    vec2 ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    float d = length( pa - ba*h );

    return  smoothstep(t/iResolution.y, 0., d ) * c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ( fragCoord - iResolution.xy) / iResolution.y;
    vec3 color = vec3(0.0);

    uv *= 10.0;
    uv.y -= iTime;

    vec2 uvi = floor(uv);
    vec2 uvf = 2.0*fract(uv)-1.0;

    vec2 p1 = vec2(-0.5, 0.5);
    vec2 p2 = vec2(0.0, 0.5);
    vec2 p3 = vec2(0.5, 0.5);
    vec2 p4 = vec2(0.5, 0.0);
    vec2 p5 = vec2(0.5, -0.5);
    vec2 p6 = vec2(0.0, -0.5);
    vec2 p7 = vec2(-0.5, -0.5);
    vec2 p8 = vec2(-0.5, 0.0);
    vec2 p0 = vec2(0.0, 0.0);



    vec3 c1 = vec3(1.0);
    float d = iResolution.y*0.12; 
    float onoff = step(random(p1), random(uvi));
    float moda = 1.4;
    float modb = 2.0;

    vec3 u1 = smoothstep(0.01, 0.05, line(uvf, p1, p2, d, c1))*step(moda, mod(random(uvi+p1+p2)*10.0, modb));
    vec3 u2 = smoothstep(0.01, 0.05, line(uvf, p2, p3, d, c1))*step(moda, mod(random(uvi+p2+p3)*10.0, modb));
    vec3 u3 = smoothstep(0.01, 0.05, line(uvf, p3, p4, d, c1))*step(moda, mod(random(uvi+p3+p4)*10.0, modb));
    vec3 u4 = smoothstep(0.01, 0.05, line(uvf, p4, p5, d, c1))*step(moda, mod(random(uvi+p4+p5)*10.0, modb));
    vec3 u5 = smoothstep(0.01, 0.05, line(uvf, p5, p6, d, c1))*step(moda, mod(random(uvi+p5+p6)*10.0, modb));
    vec3 u6 = smoothstep(0.01, 0.05, line(uvf, p6, p7, d, c1))*step(moda, mod(random(uvi+p6+p7)*10.0, modb));
    vec3 u7 = smoothstep(0.01, 0.05, line(uvf, p7, p8, d, c1))*step(moda, mod(random(uvi+p7+p8)*10.0, modb));
    vec3 u8 = smoothstep(0.01, 0.05, line(uvf, p8, p1, d, c1))*step(moda, mod(random(uvi+p8+p1)*10.0, modb));


    vec3 v1 = smoothstep(0.01, 0.05, line(uvf, p0, p1, d, c1))*step(moda, mod(random(uvi+p0+p1)*10.0, modb));
    vec3 v2 = smoothstep(0.01, 0.05, line(uvf, p0, p2, d, c1))*step(moda, mod(random(uvi+p0+p2)*10.0, modb));
    vec3 v3 = smoothstep(0.01, 0.05, line(uvf, p0, p3, d, c1))*step(moda, mod(random(uvi+p0+p3)*10.0, modb));
    vec3 v4 = smoothstep(0.01, 0.05, line(uvf, p0, p4, d, c1))*step(moda, mod(random(uvi+p0+p4)*10.0, modb));
    vec3 v5 = smoothstep(0.01, 0.05, line(uvf, p0, p5, d, c1))*step(moda, mod(random(uvi+p0+p5)*10.0, modb));
    vec3 v6 = smoothstep(0.01, 0.05, line(uvf, p0, p6, d, c1))*step(moda, mod(random(uvi+p0+p6)*10.0, modb));
    vec3 v7 = smoothstep(0.01, 0.05, line(uvf, p0, p7, d, c1))*step(moda, mod(random(uvi+p0+p7)*10.0, modb));
    vec3 v8 = smoothstep(0.01, 0.05, line(uvf, p0, p8, d, c1))*step(moda, mod(random(uvi+p0+p8)*10.0, modb));





    vec3[8] u = vec3 [](u1, u2, u3, u4, u5, u6 ,u7, u8);
    vec3[8] v = vec3 [](v1, v2, v3, v4, v5, v6, v7, v8);


    vec3 g = vec3 (0.0);
    int n = 8;
    for (int i = 0; i < n; i++) {
    g = max(g, max(u[i], v[i]));
    }

    color = g+vec3(0.1, 0.3, 0.6);

    // Output to screen
    fragColor = vec4(color,1.0);
}
