void mainImage( out vec4 d, in vec2 a )
{

   float x=.001+tan((iTime+15.)/1000.),y=cos((iTime+15.)/100.);  // *(iResolution.x/3840.)  *(iResolution.y/2160.)

   if (iMouse.x!=.0) {
     x = iMouse.x/iResolution.x/10.;
     y = iMouse.y/iResolution.y;
   };

   float t = iTime/300.;

   vec3 b = vec3(
       sin( a.x*x + t ),
       sin( a.x*x *y*y*y     + t*y ),
       sin( a.x*x *y*y*y*y*y + t*y*y )
    );

   vec3 c = vec3(
       sin( a.y*x + t ),
       sin( a.y*x *y*y*y     + t*y ),
       sin( a.y*x *y*y*y*y*y + t*y*y )
   );


   d = vec4(abs(b*c),1.0);
//   d = vec4(abs(b+c)*.5,1.0);

}
