vec2 uv;
vec3 col;

void add_cloud(float speed) {
	col += vec3(texture(iChannel1, fract(uv+iTime*speed)).r*0.5);
}

void add_spherical_fade() {
	float mask = 1. - distance(vec2(0.5, 0.46), uv);
	mask *= mask;
	mask *= mask;
	col *= mask*mask;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    uv = fragCoord/iResolution.xy;
    add_cloud(0.1);
	add_spherical_fade();
	fragColor.rgb = vec3(0.1, 0.1, 0.1) + col;
}
