float drawCircle(vec2 p, vec2 uv, float r) {
        return smoothstep(length(uv-p),r,0.8);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 10.* ( 2.* fragCoord - iResolution.xy ) / iResolution.y;
    float waves = sin(length(uv)-iTime)/4.+.25;
    vec2 c = round(uv);
    float circles = drawCircle(c, uv, waves);
    fragColor = vec4(circles);
}
