float sirkel(vec2 p, float r) {
    return length(p) - r;  
}

float healesirkel(vec2 p, vec2 n, float r) {
    if(dot(p - vec2(0.0, 0.01), n) > 0.0) {
        return dot(normalize(n), p);
    } 
    return sirkel(p, r);  
}


float pompebled(vec2 p) {
    float phi = -3.14157 * 0.25;
    mat2 kantelje = mat2(cos(phi), -sin(phi), sin(phi), cos(phi)); 

    p = kantelje * p;

	//underste stik
    float a = healesirkel(p, vec2(0.0, 1.0), 0.075);

    //twa kopkes
    a = min(a, sirkel(p - vec2(-0.0375, 0.01), 0.0375));
    return min(a, sirkel(p - vec2(0.0375, 0.01), 0.0375));
}

//alle san bleden
float pompebleden(vec2 p) {
    float mDist = 1.0;

    mDist = min(mDist, pompebled(p - vec2(0.25, 0.2)));
    mDist = min(mDist, pompebled(p - vec2(0.1, 0.35)));

    mDist = min(mDist, pompebled(p - vec2(0.5, 0.525)));
    mDist = min(mDist, pompebled(p - vec2(0.75, 0.27)));
    mDist = min(mDist, pompebled(p - vec2(0.25, 0.77)));

    mDist = min(mDist, pompebled(p - vec2(0.70, 0.9)));
    mDist = min(mDist, pompebled(p - vec2(0.85, 0.75)));

	return mDist;    
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    //de strepen fan'e flage
    vec3 col;
    if(sin((uv.x + uv.y)*11.0) > 0.0) {
        col = vec3(1.0, 85.0, 165.0) / 255.0;
    } else {
     	col = vec3(1.0);   
    }     

    //de san pompebleden
    if(pompebleden(uv) < 0.0) {
    	col = vec3(238.0, 62.0, 52.0) / 255.0;    
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
