void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float per = abs(sin(uv.y * 10. - iTime) / 10. + 0.5 - abs(cos(uv.x * 10.)));

    float offset = smoothstep(0.,per,0.2) / 50.0;

    vec4 tex = texture(iChannel0,vec2(uv.x + offset,uv.y));

    // Output to screen
    fragColor = tex;
}
