float Circle(vec2 uv, vec2 p, float r, float blur){   //p = position


    float d = length(uv-p); //distance
    float c = smoothstep(r, r-blur, d); //smoothstep

    return c;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= .5; //Remaps coordinates to -0.5 <> 0.5 (centre of screen)
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.);
   float c = Circle(uv, vec2(-0.4, 0.1), .28, 0.01); //big circle

    c -= Circle(uv, vec2(-0.4, 0.1), .075, 0.01); //left pupil

    c += Circle(uv, vec2(0.4, 0.1), .28, 0.01); //big circle

    c -= Circle(uv, vec2(0.4, 0.1), .075, 0.01); //right pupil

   float body = Circle(uv, vec2(0, -0.6), .7, 0.01); //body

   float mouth = Circle(uv, vec2(0.,-0.2), .2, .01);
   mouth -= Circle(uv, vec2(0., -0.1), .22, .01);



    body -= clamp(mouth,0.,1.);
    col = vec3(mouth);

    col = vec3(1.,1.,1.)*c;
    col += vec3(0.,1.,0.)*body;

    fragColor = vec4(col ,1.0);
}
