const vec2 center = vec2( 0.5, 0.5 );
const float longest = length( vec2( 0.0, 0.0 ) - vec2( 0.5, 0.5 ) );
const float segments = 5.;
const float rotSpeed = 1.5;
const float twistDepth = 3.;
const float twistSpeed = 1.5;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 q = ( uv - center ) / longest;

    // Output to screen
    float v = cos( atan( q.y, q.x ) * segments
              + iTime * rotSpeed
              + twistDepth * sin( iTime * twistSpeed ) * length( q ) );
    fragColor = vec4(-v, v, 0., 1.0);
}
