void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    vec2 offset;

    offset.x = cos( iTime  * -2. + uv.y * 20. ) * 0.013 + cos( iTime  * -2.3 + uv.y * 5. ) * 0.003;
    offset.y = sin( iTime  * -3. + uv.y * 20. ) * 0.013 + sin( iTime  * -2.7 + uv.y * 5. ) * 0.003;


    fragColor = texture( iChannel0, vec2( uv.x + offset.x, uv.y + offset.y ));
}
