mat2 ro (float x) {
	float s = sin(x), c = cos(x);
    return mat2(c,-s,s,c);
}
vec4 I (vec2 U) {
	vec4 Q = A(U)*vec4(.0,2,2,2);
    mat2 m = ro(.1*iTime);
    float 
        n = (A(U+vec2(0,1)).z),
        e = (A(U+vec2(1,0)).z),
        s = (A(U-vec2(0,1)).z),
        w = (A(U-vec2(1,0)).z);
    vec3 
        v = normalize(vec3(e-w,n-s,.2)),
        l = normalize(vec3(1,1,2));
    Q.xyz += .5*v;
    Q.xy *= m;
    Q.yz *= m;
    Q.zw *= m;
    Q.wx *= m;
    Q = abs(Q);

    float a = max(dot(v,l),0.);
   	Q *= (.5*exp(-10.*a)+.6*exp(-1.*a));
    return Q;
}
void mainImage( out vec4 Q, in vec2 U )
{
    #define o .5
    Q = 0.25*(I(U+vec2(o,0))+I(U+vec2(0,o))+I(U-vec2(1,o))+I(U-vec2(0,o)));
}

void mainImage( out vec4 Q, in vec2 U )
{
   	Q = A(U);
    float o=2.1, a = 3.1*fract(.1*(iTime+U.x+U.y*R.x)), c = cos(a), s = sin(a);
    mat2 M = mat2(c,-s,s,c);
    vec4 m = 0.25*(
        A(U+vec2(o,0)*M)+A(U-vec2(o,0)*M)+
        A(U+vec2(0,o)*M)+A(U-vec2(0,o)*M)
    );
    Q.x = Q.x+(1.-Q.x)*(1.2*Q.z-Q.y-Q.w);
    Q.y = mix(mix(Q.y,m.y,.3),Q.x,.03);
    Q.z = mix(mix(Q.z,m.z,.2),Q.x,.06);
    Q.w = mix(mix(Q.w,m.w,.7),Q.w,.01);


    Q = max(Q,0.);
    if(iMouse.z>0.&&length(U-iMouse.xy)<50.) Q *= 0.;
    if(iFrame<1) {
    	Q = vec4(0);
    	if (length(U-0.5*R) < 1.||U.x<1.||U.y<1.||R.x-U.x<1.||R.y-U.y<1.)
            Q = vec4(1,1,1,0);
    }
   }

#define R iResolution.xy
#define A(U) texture(iChannel0, (U)/R)
#define B(U) texture(iChannel1, (U)/R)
#define C(U) texture(iChannel2, (U)/R)
#define D(U) texture(iChannel3, (U)/R)

vec4 hash (float p) // Dave Hoskins
{
	vec4 p4 = fract(vec4(p) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+19.19);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);

}

