#define max3(a) max(a.x, max(a.y, a.z))

vec4 rgb2cm(vec3 rgb){
	float maximum = max3(rgb);
    return vec4(rgb / max(maximum, 1e-32) - maximum, exp2(-maximum));
}

vec3 cm2rgb(vec4 cm){
    float maximum = -log2(cm.a);
	return (cm.rgb + maximum) * maximum;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 chromaMaxTex = texture(iChannel0, uv);
    vec3 color = cm2rgb(chromaMaxTex);

    fragColor = vec4(color,1.0);
}

#define max3(a) max(a.x, max(a.y, a.z))

vec4 rgb2cm(vec3 rgb){
	float maximum = max3(rgb);
    return vec4(rgb / max(maximum, 1e-32) - maximum, exp2(-maximum));
}

vec3 cm2rgb(vec4 cm){
    float maximum = -log2(cm.a);
	return clamp(cm.rgb + maximum, 0.0, 1.0) * maximum;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 color = vec3(0.0);
         color = texture(iChannel0, uv).rgb;

    vec4 chromaMax = rgb2cm(color);

    fragColor = chromaMax;
}
