void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    fragColor = vec4(texelFetch(iChannel0, ivec2(fragCoord), 0 ).xxx, 1.0);
}

float cell(ivec2 p)
{
    ivec2 r = ivec2(textureSize(iChannel0, 0));

    if(p.y < 0 || p.y >= r.y)
        return fract(float(p.x) / 400.);

    return texelFetch(iChannel0, p, 0).x;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    ivec2 p = ivec2(fragCoord);

	float l = cell(p + ivec2(-1, +1));
    float m = cell(p + ivec2( 0, +1));
    float r = cell(p + ivec2(+1, +1));

    // "Diffuse" the density values between cells.
    float c = (l + r + m * 2.) / 2.;

    // "React" with the density by mapping it onto a quadratic curve.
    // It is this repeated re-mapping onto the quadratic curve which produces chaos.
    float f = 1. - (c - 1.) * (c - 1.);

    fragColor = vec4(f);
}
