vec3 GetDist(vec3 p  ) 
{	

    float d, dif1, dif2;
    vec3 res;
    vec3 pp,p1, p2,p3; 
    res = vec3(9999.0, -1.0,-1.0);


    float planeDist1 = p.y+10.0;  //piso inf
    float planeDist2 = 50.0-p.y;  //piso sup
    float planeDist3 = p.x+30.0; //pared izq
    float planeDist4 = 30.0-p.x;  //pared der
    float planeDist5 = -p.z+40.0;  //pared atras
    float planeDist6 = p.z+40.0;  //pared atras

    res =opU3(res, vec3(planeDist1,100.0,MATERIAL_NO)); //inf
    res =opU3(res, vec3(planeDist2,100.0,MATERIAL_NO)); 
    res =opU3(res, vec3(planeDist3,100.0,-1.0)); 
    res =opU3(res, vec3(planeDist4,100.0,-1.0)); 
  	res =opU3(res, vec3(planeDist5,100.0,-1.0)); 
    res =opU3(res, vec3(planeDist6,100.0,-1.0)); 


    float sdsph= sdSphere(p-vec3(5.0,-6.0,10.*sin(iTime)), 4.0);
    res =opU3(res, vec3(sdsph,-1.0,1.0)); 


    float sds1= sdSphere(p- vec3(-10.0,-4.0,10.*sin(5.0-iTime)) , 6.0 );
    res =opU3(res, vec3(sds1,-1.0,3.0)); 


    float sds2= sdSphere(p- vec3(-10.*sin(5.0-iTime),-4.0,-5.0) , 6.0 );
    res =opU3(res, vec3(sds2,201.0,MATERIAL_NO)); 



    d = res.x;

    //return (dist, id_color, id_material)
    return vec3(res.x, res.y, res.z);
}



///-----------------------------------------
vec3 LightShading(vec3 Normal,vec3 toLight,vec3 toEye,vec3 color)
{
    vec3 toReflectedLight=reflect(-toLight, Normal);
    vec3 diffuse = max(0.,dot(Normal,-toLight))*color;
    //vec3 specular = pow(max(0.,dot(Normal,normalize(-toLight-V))),100.)*vec3(1.,1.,1.); 
    float specularf=max(dot(toReflectedLight, toEye),0.0);
    specularf=pow(specularf, 100.0);
    vec3 specular =specularf*vec3(1.0);

    return diffuse + specular;
}
//------------------------------------------------



vec3 GetNormal(vec3 p) 
{
	float d = GetDist(p).x;
    //Texture of white and black in image
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).x,
        GetDist(p-e.yxy).x,
        GetDist(p-e.yyx).x);

    return normalize(n);
}


float RayMarch(vec3 ro, vec3 rd) 
{

    // The extra distance might force a near-plane hit, so
    // it's set back to zero.
    float dO = 0.; 
    vec3 dS=vec3(9999.0,-1.0,-1.0);
    float marchCount = 0.0;
    vec3 p;

    //Determines size of shadow
    for(int i=0; i<MAX_STEPS; i++) 
    {
    	p = ro + rd*dO;
        dS = GetDist(p);

        if(dO>MAX_DIST || abs(dS.x)<MIN_DIST) break;
        dO += dS.x;
        //marchCount+= 1./dS.x*.75;
        marchCount++;
    }

    mObj.dist = dO;
    mObj.id_color = dS.y;
    mObj.marchCount=marchCount;
    mObj.id_material=dS.z;
    mObj.normal=GetNormal(p);
    return dO;
}

