vec3 mirror(inout vec3 p, inout vec3 n, inout vec3 rd, int opmat) {
	p += n * MIN_DIST * 2.;
	rd = reflect(rd, n);

	float l = RayMarch(p, rd);
	p = p + rd * l;
	n = GetNormal(p);

	//mat = material(p);
	//return shadow(p, n, l, rd) * vec3(0.6,0.3,0.8);
    vec3 col=Getluz( p,mObj.ro,rd, n, vec3(1.0) ,light_pos1);

    return col;
}


//-------------------------------------

float mandelbrot(vec2 c)
{
    vec2 z = vec2(0.0);
    const float B = 256.0;
    float l = 0.0;
   for (int i = 0; i < 512; i++)
    {
        z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + c;
        if (dot(z, z) > (B * B))
            break;
        l += 1.0;
    }
    if (l > 511.0)
        return (0.0); 
    //float l = l - log(log(length(z))/log(B))/log(2.0);
    l = l - log2(log2(dot(z, z))) + 4.0;
    return (l);

}
