vec3 getMaterial( vec3 pp, float id_material,vec3 ro, vec3 rd)
{
  vec3 col=vec3(1.0);
  vec3 p=pp;



    if (id_material==1.0)
    {


        //esfera rotada
        vec3 color =vec3(0.1,0.8,0.7);
        p-=vec3(0.0,0.0,10.0*sin(iTime));

        //p.xz*=rot2D(atan(cos(iTime),sin(iTime)));

        p.yz*=rot2D(-1.0*sin(iTime));

        p=mod(p,2.);
        p-=1.;


        float arc =atan(p.y,p.x);

        float v=0.85/(length(p)+0.2*sin(arc*8.));
        v=max(pow(v,2.),1.);
        col*=color*vec3(v,0.9*v,0.3*v);
        return col;
      }


    if (id_material==2.0)
    {

       return vec3(abs(sin(dot(mObj.normal,vec3(1.0))*PI*3.0)));	      

    }


    if (id_material==3.0)
    {

            float rot = mod(sin(-iTime * 1.0),radians( 360.0));


			mat3 rotY = mat3(
        		vec3(1.0, 0.0, 0.0),
        		vec3(0.0, cos(-rot), sin(-rot)),
        		vec3(0.0, -sin(-rot), cos(-rot)));

            vec3 pos = p + 0.001 *rd;
			vec3 normal = GetNormal(pos);

            vec3 v3 = rotY* normal;

            vec2 v2 = 1.5 * v3.xy / abs(v3.z);
            float dMb = mandelbrot(v2);


    		vec3 material = 0.44 + 0.5 * cos(3.0 + dMb * 0.11 + vec3(0.0, 0.5, 1.0)); 

            col=material;
        	return col;
     }

}
