float d(vec3 p)
{
    float a = pow(p.x,2.) + pow(p.y-1.,2.) + pow(p.z,2.);
    a *= 1.75;
    a = pow(a,5.);
    return clamp(a,0.,1.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy;

    vec4 texColorVideo1 = texture(iChannel0,xy);
    vec4 texColor2Video2 = texture(iChannel1,xy);

    float a = d(texColorVideo1.rgb);

    if (iMouse.x > fragCoord.x){
    	fragColor = vec4(a);
    } else {
	    fragColor = texColor2Video2 * (1.-a) + texColorVideo1 * a;
    }
}
