#define PI  3.141592
#define TAU 6.283184


vec2 cs(float a){
    return vec2(cos(a),sin(a));
}

vec2 rot(vec2 v, float a) {
    vec2 c = cs(a);
    return v * mat2x2(c.x, -c.y, c.y, c.x);
}

vec2 sp(float t){
    return vec2(floor(t), fract(t));
}

float rand(vec2 co){
  return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}


vec2 pt(float i) {
    return vec2(sin(i*i), cos(i*i));
    //return normalize(vec2(rand(vec2(i   ,0)),rand(vec2(i   ,1))) * 2. - 1.);
}
vec2 interpolate(vec2 a, vec2 b, vec2 c, vec2 d, float t){
    vec2 p0 = b;
    vec2 m0 = (b-a)*2.;
    vec2 p1 = c;
    vec2 m1 = (c-b)*2.;

    float t2 = t*t;
    float omt = 1.-t;
    float omt2 = omt*omt;
    float h00 = (1.+2.*t)*omt2;
    float h10 = t*omt2;
    float h01 = t2*(3.-2.*t);
    float h11 = t2*(t-1.);

    return h00*p0 + h10*m0 + h01*p1 + h11*m1;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.-iResolution.xy)/min(iResolution.y,iResolution.x)*2.;


    float i = floor(iTime*.3);
    float j = fract(iTime*.3);


    vec2 a = pt(i-1.); 
    vec2 b = pt(i); 
    vec2 c = pt(i+1.);
    vec2 d = pt(i+2.);
    float t = j;
    vec2 p = interpolate(a,b,c,d,t);


    c = interpolate(b,c,d,pt(i+3.), t);
    b = interpolate(a,b,c,d, t);

    float dist = length(uv-p);
    fragColor = vec4(1);

    if(length(uv-b)*2. < dist) {
        dist = length(uv-b)*2.;
        fragColor = vec4(1,0,0,0);
    }

    if(length(uv-c) < dist) {
        dist = length(uv-c);
        fragColor = vec4(0,1,0,0);
    }

    fragColor*= 1.0 - dist*10.;
    //fragColor.gb = vec2(fragColor.r);
}
