const float PI = 3.14159265;
//use for differential
const float EPS = 0.001;


//tracing result structure
struct TraceData {
	vec3 normal;
    float t;
    float nearestT;
    float nearestD;
    int uniqueID;

};

//common function
mat2 genRot(float val){
	return mat2(cos(val), -sin(val),
               sin(val), cos(val));
}

float rand (float x){
    x = fract(sin(x*416.31434));
    x = fract(sin(x*234.41523));
    x = fract(sin(x*235.51424));
	return x;
}

float smin( float a, float b, float k )
{
    float res = exp2( -k*a ) + exp2( -k*b );
    return -log2( res )/k;
}

vec3 pmod(vec3 p,float c){
    float tmp = PI * 2. / c;
	float l = length(p.xy);
    float theta = atan(p.y/p.x);
    theta = mod(theta,PI * 2. / c);
    return vec3(l * cos(theta), l * sin(theta),p.z);

}


//Common SDF

float sphere(vec3 p,vec3 o,float r){
	return length(p - o) - r;
}

float cylinder(vec2 p,vec2 o,float r){
	return length(p - o) - r;
}

float cube(vec3 p,vec3 o,vec3 s){
    float x = abs(p.x - o.x) - s.x/2.;
    float y = abs(p.y - o.y) - s.y/2.;
    float z = abs(p.z - o.z) - s.z/2.;
    return max(x,max(y,z));
}

float line(vec3 p,vec3 q1,vec3 q2,float r){
	float t = clamp(
    	dot(q2 - p,q2 -q1)/dot(q2-q1,q2-q1),
    	0.,
        1.
    );
    vec3 q = t * q1 + (1. - t) * q2;
    return length(q - p) - r;
}


//Gathering SDF

float map(vec3 p){
    float fs = 8.;
    p = (fract(p / fs + 0.5) - 0.5)*fs;
    p.xz *= genRot(iTime);
    p.xy *= genRot(PI/8.);
    return min(sphere(p,vec3(-0.25),1.2),sphere(p,vec3(0.25),1.2));
}

//Getting Normal

vec3 getNormal(vec3 p) {
    return normalize(vec3(
        map(p + vec3(EPS, 0.0, 0.0)) - map(p + vec3(-EPS,  0.0,  0.0)),
        map(p + vec3(0.0, EPS, 0.0)) - map(p + vec3( 0.0, -EPS,  0.0)),
        map(p + vec3(0.0, 0.0, EPS)) - map(p + vec3( 0.0,  0.0, -EPS))
    ));
}

//Setting CameraPos
vec3 Camera(float t){
	return vec3(4.,4.,-5. + iTime * 2.);
}

//Setting Ray
vec3 Ray(vec2 uv, float z){
	vec3 ray = normalize(vec3(uv,z));
    ray.xz *= genRot(PI/8.);
    ray.yz *= genRot(PI/8.);
    ray.xy *= genRot(iTime/8.);
    return ray;
}

//Tracing Ray

TraceData trace (vec3 o, vec3 r){
    TraceData data;
	float t = 0.0;
    float nearestT = 10000000.0;
    float nearestD = 10000000.0;
    vec3 p = vec3(0.0,0.0,0.0);

    for(int i = 0; i < 128; ++i){
        p = o + r * t;
        float d = map(p);
        t += d * 0.25;
        nearestT = d < nearestD ? t : nearestT;
        nearestD = min(nearestD,d);

    }
    data.nearestD = nearestD;
    data.nearestT = nearestT;
    data.normal = getNormal(p);
    data.t = t;
    return data;
}

//Making color
vec3 getColor(vec3 o,vec3 r,TraceData data){
    float t = data.t;
    float nT = data.nearestT;
    float nD = data.nearestD;
    float fog = 1.0 / (1.0 + t * t * 0.005);
    float a = dot(data.normal,r);
    vec3 p = o + r * t;
    vec3 fc = vec3(0.05);
    fc = mix(fc,vec3(1.),1. + a*1.5);
    vec3 nP = o + r * data.nearestT;
    fc = mix(vec3(0.05),fc,fog);
    fc = clamp(fc,0.,1.);
    float at = atan(r.y/r.x)/PI + 0.5;
    vec3 ccol = vec3(sin(at * PI), 1. - sin(at * PI),sin(p.z) + 0.1);
    ccol = mix(ccol,vec3(0.0),sin(nP.z * 2. + iTime * 2.)/2. + 0.5);
    fc += ccol / (1. + max(nT * nT * 0.0001, nD *nD * 10.0));
    //fc = vec3(fog);
    return fc;
}
//Drawing

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //set canvas
    vec2 uv = fragCoord.xy /iResolution.xy;
	uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    //set camera and ray
    vec3 r = Ray(uv,1.2);
    vec3 o = Camera(iTime);

    //trace ray
    TraceData data = trace(o,r);
    vec3 fc = getColor(o,r,data) ;
	//fc = vec3(fog);
    // Output to screen
    fragColor = vec4(fc,1.0);
}
