vec2 complexSq(vec2 z) {

	return vec2(z.x * z.x - z.y*z.y, 2.*z.x*z.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 c = (fragCoord - vec2(iResolution.x / 2., iResolution.y / 2.)) / iResolution.x ;
    c *= 4. * iMouse.y/iResolution.y * iMouse.y/iResolution.y + 0.001;// * (1. + sin(iTime));
    //c -=  2.*vec2(iMouse.x/iResolution.x - 0.5,iMouse.y/iResolution.y - 0.5 );
    c-= vec2(0.6937, 0.7);

    vec2 z = vec2(0,0);


    float j = 0.;
    while (length(z)< 2.) {
      z = complexSq(complexSq(complexSq(z))) + c;
      j += 1.;
      if (j >= 2000.)
          break;
    }

    fragColor = vec4(vec3(z.x,z.y,1) * j / 50. * ((iMouse.y/iResolution.y) * (iMouse.y/iResolution.y) + 1.0 ) ,1);
}
