float circle(in vec2 _st, in float _radius){
    vec2 dist = _st-vec2(0.5);
	return 1.-smoothstep(_radius-(_radius*0.01),
                         _radius+(_radius*0.01),
                         dot(dist,dist)*4.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = (fragCoord/2.0  - 0.1 * iResolution.xy) / iResolution.y+0.35;

    //background gradient colour
    float pct = 1.8-st.y*1.6;

    //the loop repeats 50 times, so there are 50 circles drawn in total
    for(int i=0; i<50;i++){
        //change i to float so I can multiply by it
        float i_float = float(i);
        //here the circle function is called. The location (position) of each circle is 
        //adjusted by the sin of the time value.
        //The 50 circles are in different positions because the incremented i_float 
        //value is used:

        vec2 position = st+(0.2*sin(3.-i_float*st.yx*((sin(iTime/3.)+1.))));
        pct += circle(position,0.001)*st.y*1.5;
    }

	pct=pct-1.;

    // Output to screen, adjust colour
    fragColor = vec4(pct+.1,pct+.1,pct,1.0)*vec4(0.9,0.,0.,0.)+vec4(0.,0.0,0.3,0.);
}
