// from https://thebookofshaders.com/06/
float rect(in vec2 st, in vec2 size){
	size = 0.25-size*0.25;
    vec2 uv = smoothstep(size,size+size*vec2(0.002),st*(1.0-st));
	return uv.x*uv.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float y = uv.y;
    float x = uv.x;

    vec3 box_col = vec3(0.745,0.678,0.539);

    vec3 orange = vec3(0.980,0.576,0.113);
    vec3 blue = vec3(0.653,0.918,0.985);

    vec3 col = vec3(0.);

    col += mix(orange, blue, step(x, 0.5));

    vec2 rect_s = vec2(0.01, 0.25);

    vec2 rhsP = vec2(uv.x - 0.25, uv.y);
    float rhsR = rect(rhsP, rect_s);

    vec2 lhsP = vec2(uv.x + 0.25, uv.y);
    float lhsR = rect(lhsP, rect_s);

    col = mix(col, box_col, rhsR);
    col = mix(col, box_col, lhsR);

    // Output to screen
    fragColor = vec4(col,1.0);
}
