// variant of https://shadertoy.com/view/ttlcDs

void mainImage(out vec4 O, vec2 u) {
    vec2 R = iResolution.xy*.1, V = u/R.y, d, C,
    U = V*5. + V.y* cos( iTime + V.x*.56 + 11.*O.xw );
    for (int k=0; k<25; k++)
        C = round((U+vec2(k%5-2,k/5-2))*.2),
        V = ( U- C/.2 )
           * inverse(transpose(mat2(dFdx(U),dFdy(U)) ))/R.y,
        d = max(V-V,1.-.05*dot(U- C/.2,U- C/.2)),
        O += sin( 36.*length(V) -iTime*0.+C.x) * (d*d*d*d).x *.5+.5;
    O *= smoothstep(1.,-1.,U.y -37.)/25.;
}
