// Baked noise sampler
float noise(in vec2 x) {
    vec2 p = floor(x);
    vec2 f = fract(x);
    f = f*f*(3.0-2.0*f);
    float a = textureLod(iChannel0, (p + vec2(0.5, 0.5)) / 256.0, 0.0).x;
	float b = textureLod(iChannel0, (p + vec2(1.5, 0.5)) / 256.0, 0.0).x;
	float c = textureLod(iChannel0, (p + vec2(0.5, 1.5)) / 256.0, 0.0).x;
	float d = textureLod(iChannel0, (p + vec2(1.5, 1.5)) / 256.0, 0.0).x;
    return mix(mix( a, b, f.x), mix(c, d, f.x), f.y);
}

const mat2 mtx = mat2( 0.80,  0.60, -0.60,  0.80 );

// 1D 6 octave FBM noise
float fbm6(vec2 p) {
    float f = 0.0;

    f += 0.500000 * noise(p); p = mtx*p*2.02;
    f += 0.250000 * noise(p); p = mtx*p*2.03;
    f += 0.125000 * noise(p); p = mtx*p*2.01;
    f += 0.062500 * noise(p); p = mtx*p*2.04;
    f += 0.031250 * noise(p); p = mtx*p*2.01;
    f += 0.015625 * noise(p);

    return f/0.96875;
}

// 2D 6 octave FBM noise
vec2 fbm6_2(vec2 p) {
    return vec2( fbm6(p+vec2(9.2)), fbm6(p+vec2(5.7)) );
}

// Domain Warped Noise
vec2 pattern( in vec2 p) {
    return fbm6_2(p + 8.0 * fbm6_2(p + 8.0 * fbm6_2(p + vec2(sin(iTime / 2.0), cos(iTime / 2.0)))));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalised Screen coordinates
    vec2 uv = fragCoord/iResolution.xy;

    // Sample an offset using domain warped noise
    vec2 coord = pattern(uv * 4.0);

    // Use that to make a displaced sample of the image
    fragColor = texture(iChannel1, uv + 0.2 * (coord - 0.5));
}
