vec2 cmplx_mul(vec2 one, vec2 two) {
    float a = one.x; float b = one.y;
    float c = two.x; float d = two.y;
 	return vec2(a*c - b*d, b*c + d*a);    
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const vec4 introDest = vec4(400, 300, 0, 0);
    const vec4 introStart = vec4(0, 0, 0, 0);


    const int MAX_STEP = 1000;
    vec2 c = vec2(0.21, 0.0001);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy) - vec2(0.5);


    vec4 eMouse = iMouse - introDest;
    //vec4 eMouse = introDest;


    if(iTime < 555.0) {
        float time = 0.92 + iTime / 5555.0;
     	eMouse = introStart * (1.0-time) + introDest * time;   

    }


    vec4 mouse = eMouse / 1000.0;


	c = mouse.xy;

    float escapeThresh = 5.0 + sin(iTime * 1.0) * 4.0;
    //float escapeThresh =15.0;

    for(int i = 0; i < MAX_STEP; i++) {
        uv = (cmplx_mul(uv, uv) + c);
        float len = uv.x*uv.x + uv.y*uv.y;
        if(len >= escapeThresh) break;
    }

    float len = length(uv);
    int leni = int(len * 100.0);

    int shifto = 0xffff << leni;
    vec3 col = vec3((shifto >> 16) & 0xff, (shifto >> 8) & 0xff, shifto & 0xff) / 255.0;

    	fragColor = vec4(col,1.0);


}
