float circle(vec2 uv, float strength)
{    
    vec2 p = iResolution.xy * 0.5;  
    vec2 c = iResolution.xy * uv;  
    float r = iResolution.y * strength;
    float circle = (length(p - c) - r) / iResolution.y;
    return circle;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Scenes
    vec4 scene1 = texture(iChannel0, uv); 
    vec4 scene2 = texture(iChannel1, uv); 

    float strength1 = min(2.0 * fract(iTime * 0.05), 1.0);
    // float strength2 = min(2.0 * fract(iTime * 0.05), 1.0);

    // Mask
    float mask1 =       smoothstep(0.0, 0.1, circle(uv, strength1));
    float mask2 = 1.0 - mask1;//smoothstep(0.0, 0.1, circle(uv, strength2));

    // Output to screen
    fragColor = scene1 * mask1 + scene2 * mask2;
}
