//shader learning attempt #1: Some square animation.
float Square2D(vec2 uv, float scale)
{
   vec2 bl = step( .05*scale ,min(uv,1.-uv));
    return bl.x * bl.y;
}

float Square2DOutline(vec2 uv, float scale)
{
    uv = min(uv,1.-uv);
    return smoothstep( .004 , 0., abs( min(uv.x,uv.y) - .05*scale ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	float T =iTime*0.7;// fract(sin(iTime));
    float anim = T;
    //vec3 color = vec3(0.0);

    uv = min(uv,1.-uv);
    float i = round( ( anim +  min(uv.x,uv.y) / .05 ) *2. ) / 2.; 
    fragColor = vec4( Square2DOutline(uv, i - anim) );
}
