#define UV_ORIGIN 0.5
#define ZOOM 1.0
#define SPEED 3.0

const float PI = 3.14;

mat2 rotationMatrix(float angle)
{
	angle *= PI / 180.0;
    float s=sin(angle), c=cos(angle);
    return mat2( c, -s, 
                 s,  c );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Establish the origin of the texture 
    vec2 uv = (fragCoord-UV_ORIGIN*iResolution.xy)/iResolution.y;
    // Rotate the UVs and zoom in or out
    uv *= rotationMatrix( SPEED * iTime ) * ZOOM;
    vec4 col = texture( iChannel0, uv );
    fragColor = col;
}
