#define PI 3.1415926535

vec2 rot2(float a, vec2 uvr) {

    a *= PI * 2.;
    float c = cos(a);
	float s = sin(a);
    return vec2(uvr.x*c - uvr.y*s, uvr.x*s + uvr.y*c);
}

mat2 rot(float a) {

    a *= PI * 2.;
    float c = cos(a);
	float s = sin(a);
    return mat2(c, -s, s, c);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

   	vec2 uv = ((fragCoord - iResolution.xy*.5) /iResolution.y*2.);


    //uv *= rot(iTime / 8.);
    uv = rot2(iTime / 8., uv);

	fragColor = texture(iChannel0, uv);
}
