const float PI = 3.14159265;

// from https://iquilezles.org/articles/distfunctions

float RoundRect( vec2 p, vec2 b, float r )
{
    return length(max(abs(p)-b+r,0.0))-r;
}


float size = 0.4;
float highlight = 0.004;
float grad = 0.02;
float roundness = 0.001;
float sharpness = 4.;


vec3 colLow0 = vec3(1, 0, 0);
vec3 colLow1 = vec3(0, 1, 0);

vec3 colHigh0 = vec3(1, 0.9, 0.9);
vec3 colHigh1 = vec3(0.9, 1, 0.9);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    

    vec2 uv = fragCoord/iResolution.xy;

    vec2 uv_center = uv - 0.5;
    float as = iResolution.x / iResolution.y;
    uv_center.x *= iResolution.x / iResolution.y;

    float rr = RoundRect(uv_center, vec2(size), roundness);

    float inner = smoothstep(0., grad, rr);
    float outer = smoothstep(highlight + grad * 2., highlight + grad, rr);

    float val = inner * outer;
	val = pow(val, sharpness);


    float colFactor = sin(uv.y * 5. + iTime * .5) * 0.5 + 0.5;

    vec3 colLow = mix(colLow0, colLow1, colFactor);
    vec3 colHigh = mix(colHigh0, colHigh1, colFactor);

    vec3 col = mix(colLow, colHigh, val);

    float blink = 1. - step(sin(iTime * 2.), -0.9) * (sin(iTime * 50.) * 0.5 + 0.5);


    col = col * val * blink;

    fragColor = vec4(col, 1.);
}
