void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = texture(iChannel0,uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 org = fragCoord.xy / iResolution.x;
    vec2 mouse = iMouse.xy / iResolution.x;

    float r = length(org-mouse);
    float s = 1.0 - smoothstep(0.0,0.03,r);

    vec3 color = texture(iChannel0,uv).xyz;
    if(iMouse.z > 0.0)
    {
        color = mix(color,texelFetch( iChannel1, ivec2(65,0), 0 ).x > 0.0 ? vec3(0,0,0) : vec3(1,1,1),s);
    }

    fragColor = vec4(color,1.0);
}
