float Noise(vec2 p){
    return fract(sin(p.x*154.+p.y*654.)*55655.+iTime*0.04);
}
float SmoothStepNoise(vec2 p){
    vec2 lv = smoothstep(0.0,1.0,fract(p));
    vec2 id = floor(p);
    float br = Noise(id);
    float bl = Noise(id+vec2(1.,0.));
    float b = mix(br,bl,lv.x);

    float tr = Noise(id+vec2(0.,1.));
    float tl = Noise(id+vec2(1.,1.));
    float t = mix(tr,tl,lv.x);

    return mix(b,t,lv.y);
}
float SmoothStepNoise2(vec2 uv){
float c = SmoothStepNoise(uv*4.);
    c += SmoothStepNoise(uv*8.)/2.;
    c += SmoothStepNoise(uv*16.)/4.;
    c += SmoothStepNoise(uv*32.)/8.;
    c += SmoothStepNoise(uv*64.)/16.;
    c += SmoothStepNoise(uv*128.)/32.;
    c += SmoothStepNoise(uv*256.)/64.;
    c += SmoothStepNoise(uv*512.)/128.;

    return c/=1.5;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
		//uv+=iTime*0.1;
	float c = SmoothStepNoise2(uv);

    vec3 col = vec3(c);

    fragColor = vec4(col,1.0);
}
