//fake random
float rand(in vec2 uv) {
    float a = dot(uv, vec2(92.0, 80.0));
    float b = dot(uv, vec2(41.0, 62.0));
    float x = (sin(a) + cos(b)) * 51.0;
    return fract(x) - 0.5;
}
//Thanks for mds2 ,it looks more aesthetically pleasing now!
vec2 rand2(in vec2 uv) {
    return vec2(rand(uv), rand(uv + vec2(0.1)));
}

vec2 almost_gauss_rand(in vec2 uv) {
    return 0.25 * (rand2(uv) + rand2(uv + 4.7) + rand2(uv + 9.1) + rand2(uv + 12.3));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 rnd = almost_gauss_rand(uv) ;
    uv += rnd * 0.05;
    vec3 col = texture(iChannel0, uv).rgb;
    col.rb += 0.2 * almost_gauss_rand(uv + vec2(0.3, -4.0) + sin(iTime));

    col.bg += 0.2 * almost_gauss_rand(uv + vec2(0.3, -4.0) + cos(0.7 *iTime));
    col *= vec3(0.9, 0.95, 1.0);
    fragColor = vec4(col, 1.0);
}
