const float PI = 3.14159265;
const float loop = 5.;

float fold( float x )
{
    return abs( 1. - mod( x, 2. ));
}

float stripe( vec2 uv, float angle, float scale, float thickness)
{
    return smoothstep(
        thickness, thickness * 0.8, fold(
            dot(
                uv * scale, vec2(sin(angle), cos(angle))
        	)
        )
    );
}

float cycle(float x, float t)
{
    float trans = smoothstep(1., 0.98,
        abs(t) / (0.5 * loop)
    );
    return x * trans + 2.0 - 2.0 * trans;
}

float spikes(float x) {
    x = 1. - abs(sin(x));
    return x;
}

float spiral(float dist, float angle)
{
    return smoothstep(0.52, 0.5, fold(
    	dist + angle / PI
    ));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const vec3 backCol = vec3(0.02,0.13,0.11);
    const vec3 stripe1Col = vec3(0.97, 0.11, 0.14);
    const vec3 spiral1Col = vec3(0.3, 1., 0.65);   
    float time = floor(iTime * 60.) / 60.;
    float time1 = mod(time, loop) - loop * 0.5;
    float time2 = mod(time - loop * 0.5, loop) - loop * 0.5;

    // Normalized pixel coordinates
    float scale = 0.5 * distance(vec2(0), vec2(iResolution.x, iResolution.y));
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / scale;
    float angle = atan(uv.y, uv.x);

    vec2 uv1 = uv * (1. - 0.3 * spikes(angle * 8. - time1 * 0.1));
    uv1.y += sin(uv1.x * 11. + time1) * 0.02 + sin(uv1.x * 32. - 2.2 * time1) * 0.01;

    float stripe1 = cycle(
        0.5 * stripe(uv1 + vec2(0.0, time1 * 0.2), time1 * -0.1, 6., .08) +
        0.7 * stripe(uv1 + vec2(0.0, 0.5 + time1 * 0.1), time1 * 0.02, 18., .08)
        , time1);

    uv.x += sin(uv.y * 9. - time2) * 0.03 + sin(uv.y * 23. + time2 * 3.5) * 0.01 ;
    float dist = log(uv.x*uv.x+uv.y*uv.y);
    float prog = time2 / loop;
    float spiral1 = cycle(0.15 * spiral((1.0 + 12.0 * (prog * prog)) * (dist + 4.0), angle + (0.5 * time2 + 2. * sin(PI * prog))), time2);

    vec3 color = backCol
        + stripe1 * stripe1Col
        + spiral1 * spiral1Col;
    // Output to screen
    fragColor = vec4(
        color,
        1
    );
}
