void mainImage( out vec4 O, vec2 U )
{
    vec2 u = U/iResolution.x;

    u.y += sin(u.x*25.3)*.03 + iTime*.1;
    u *= vec2(30,10);

    float x = u.x + iTime + floor(u.y) * 23.,
          y = fract(u.y),
          f = .5 + sin(x*.22)*.2,
          w = 1. - sin(x-cos(x))*f  
                 - sin(x*1.7)*.1,
          k = smoothstep(w*.9,w,y*2.);    

    O = vec4( (y-k) *1.5 );
}

