const float e = 2.7182818284590452353602874713527;
float noise(vec2 texCoord)
{
    float G = e + (iTime * 0.1);
    vec2 r = (G * sin(G * texCoord.xy));
    return fract(r.x * r.y * (1.0 + texCoord.x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5;
    uv *= 13.0;

    float t = iTime+155.;
    uv -= t*vec2(3.1,3.5);
    // Time varying pixel color
    vec4 col;
    for (int i=0; i<7; i++) {
        t *= 1.13879213724+sin(col.r+col.g+col.b)*0.0052863;
	    col.r += sin(uv.x*0.4+t);
	    col.g += cos(uv.y*0.4+t*1.1379);
        col.rgba = col.gbar;
    }

    vec2 g = fragCoord;
    g /= 725.0+67.0*sin(0.1*iTime);
    vec4 f = smoothstep(.2,.7,sin(g.xyxx+cos(iDate.w*.7+g.yxxx))
                        *cos(g.yxyx+sin(iDate.w*.1   +g.yyxx)));

                        col.rgb = clamp(col.rgb/7.0+0.5,0.0,1.0);
    // Output to screen
    float n = noise(uv);
    fragColor = (0.3*f) + (0.8-0.5*f)*vec4(n>col.a?col.rgb:col.rgb*0.977+0.033*col.brg,1.0);
}
