//--------------------------------------------------------------------------

float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx+37.38);
    return step(fract((p3.x + p3.y) * p3.z), .4);
}

//--------------------------------------------------------------------------
float ring(vec2 p, vec2 uv, float r1, float r2)
{
    float d = length(uv-p);
    return step(d, r2) - step(d, r1);
}

//--------------------------------------------------------------------------
void mainImage( out vec4 fragColour, in vec2 fragCoord)
{
 	vec2 uv = (-iResolution.xy + 2.0 * fragCoord ) / iResolution.y;
    uv = floor(uv*100.0)/100.0;

    float red = ring(vec2(0,0), uv, .7, .98);
    red += ring(vec2(0), uv, .0, .25);
    float blue = ring(vec2(0), uv, .34, .61);
    vec3 col = vec3(red, 0.0, blue)*.9;
    if (red+blue > 0.0) col+=.1;

    fragColour = vec4(col * hash12(uv*iResolution.y), 1.0);
}
