#define t iTime
#define r iResolution

void mainImage( out vec4 o, in vec2 U )
{
    vec3 u=vec3(2.*U-r.xy,r.y);
    u=normalize(vec3(u.xy,sqrt(max(u.z*u.z-dot(u.xy,u.xy)*3.,0.))));
    for(int i=0;i<6;i++)
    {
        float l=length(u);
        u.x+=sin(u.y+t*.3)*5.;
        u.y+=cos(u.x+t*.1+l);
        o=cos(l+vec4(.3,.1,0,0));
    }
}
