void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalize UV Coordinates
    vec2 uv = (fragCoord.xy-0.5*iResolution.xy)/iResolution.y;

    // Radial
    uv = vec2(length(uv), atan(uv.x, uv.y) / 3.14159265 + iTime * .3);

    // Pattern
    vec2 pattern = vec2(.3, 2.5); // => Change values to change pattern
    uv = fract(uv * pattern);

    // Color
    vec3 col = vec3(uv.y, uv.x, .5);

    // Add shadow
    col *= 1. - vec3(col.r) * .9;

    // Enjoy
    fragColor = vec4(col,1.0);
}
