// Instructions: click and drag your mouse on screen

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;


    vec3 bufferA = texture(iChannel0, uv).rgb;
    vec3 col = vec3(bufferA);

    fragColor = vec4(col,1.0);
}

#define BRUSH_RADIUS 0.2
#define BRUSH_OPACITY 0.1
#define FADE_DECAY 0.01

float circle(vec2 uv, vec2 pos, float r){
    float d = length(pos - uv);
    return smoothstep(r, r-d, d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ratio = iResolution.x/iResolution.y;
    vec2 uv = fragCoord/iResolution.xy;

    vec3 bg = texture(iChannel1, uv).rgb;

    vec3 col = texture(iChannel0, uv).rgb;
    vec2 mouse = iMouse.xy/iResolution.xy;

    uv.x *= ratio;
    mouse.x *= ratio;

    if(iMouse.z > 0.0)
    {
        col += vec3(circle(uv, mouse, BRUSH_RADIUS)) * bg * BRUSH_OPACITY;

    }

    col -= FADE_DECAY;
    col = clamp(col, vec3(0.), bg);


    fragColor = vec4(col,1.0);
}
