void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   float wideX = 0.05;
   float wideY = 0.08;

   float centerLineX = 0.65;
   float centerLineY = 0.5;

   vec2 ourCoordinates = vec2(fragCoord.xy / iResolution.xy);

   vec3 yellowLine = vec3(1.0, 1.0, 0.0);

   vec3 backgroundColor = vec3(0.0, 0.0 , 1.0);

   if(abs(ourCoordinates.x - centerLineX) < wideX) backgroundColor = yellowLine;
   if(abs(ourCoordinates.y - centerLineY) < wideY) backgroundColor = yellowLine;

   fragColor = vec4(backgroundColor, 1.0);
}
