const float iSqrt2Pi = 0.997355701;

vec3 Heatmap2(float x) {
    vec3 t = vec3(1, 0.5, 0) - x;
    return iSqrt2Pi * exp(-12.5 * t * t);
}

vec3 Heatmap(float x) {
    vec3 t = vec3(4, 2, 0) - 4.0 * x;
    return x * clamp(2.0 - abs(t), 0.0, 1.0);
}

float Greyscale(vec3 x) {
    return dot(x, vec3(0.3, 0.59, 0.11));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    vec3 t = texture(iChannel0, uv).xyz;
    bool usingHeatmap = iTime - 2.0 * floor(0.5 * iTime) > 0.5;
    float grey = Greyscale(t);
    //vec3 col = usingHeatmap ? Heatmap(grey) : t;
    vec3 col = usingHeatmap ? vec3(grey) : t;

    // Output to screen
    fragColor = vec4(col, 1.0);
}
