const float NUM_LINES = 10.; 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec4 img = texture(iChannel0, uv);
    float progress = abs(sin(iTime * 0.75));
    //float progress = iMouse.x/iResolution.x;
    float lineW = 1.0 / NUM_LINES;
    float count = 0.;

    if ( progress > uv.x) { //slide hori
    for (float i = 0.; i < 1.0; i += lineW){
        count += 1.;
        //img = mix( img, vec4(1.,0.,0.,0.5), step(0.1 * float(i),uv.x));
        if ((uv.x > i) && (uv.x < i + lineW * progress)) {
            if (mod(count, 2.0)==0.0){
            	img = vec4(0.0,0.0,0.0,1.0);
            }else {
            	img = vec4(1.0,1.0,1.0,1.0);
            }
        }
    }
    }

