mat2 rotate(float r){return mat2(cos(r),sin(r),-sin(r),cos(r));}
vec3 rotate(vec3 c,float r){return vec3(rotate(r)*c.xz,c.y).xzy;}
float de(vec3 c){
    vec2 r = iChannelResolution[1].xy;
    float p = length(texelFetch(iChannel1,ivec2(mod(c.xz/2.,vec2(r.x/r.y,1.))*r.y),0))/300.;
    return min(c.y+1.,length(c-vec3(0,0,3))-.3)+p;
}
vec4 doMarch(vec3 pos,vec3 cam){
    int i = 0;
    vec3 march = vec3(0);
    float d = 1.;

    for(;i<100&&d>0.02&&length(march)<100.;i++){
        d = de(pos+march);
        march += d*cam;
    }
    return vec4(march+pos,i);
}
vec3 getnormal(vec3 n){
    vec2 e = vec2(1.0,-1.0)*(n.y<0.002-1.?.002:.002);
    return normalize(e.xyy*de(n+e.xyy)+e.yyx*de(n+e.yyx)+e.yxy*de(n+e.yxy)+e.xxx*de(n+e.xxx));
}
void mn(out vec4 o,vec2 u,float mmin){
    vec2 uv = (u-iResolution.xy/2.)/iResolution.y;
    vec3 pos = vec3(0);
    vec2 look = vec2(0,0);
    vec3 cam = rotate(rotate(normalize(vec3(uv,1)).yxz,-look.y).yxz,look.x);

    vec4 march = doMarch(mmin*cam,cam);

    if(length(march.xyz)<100.){
        vec2 r = iChannelResolution[1].xy;
        o = texelFetch(iChannel1,ivec2(mod(march.xz/2.,vec2(r.x/r.y,1.))*r.y),0);
        o *= texture(iChannel3,reflect(cam,getnormal(march.xyz)));
    } else 
        o = texture(iChannel2,cam);

}
void mainImage(out vec4 o,vec2 u){
    float m1 = length(texelFetch(iChannel0,ivec2(u/4.),0).xyz);
    float m2 = length(texelFetch(iChannel0,ivec2(u/4.)+ivec2(0,1),0).xyz);
    float m3 = length(texelFetch(iChannel0,ivec2(u/4.)+ivec2(0,-1),0).xyz);
    float m4 = length(texelFetch(iChannel0,ivec2(u/4.)+ivec2(1,0),0).xyz);
    float m5 = length(texelFetch(iChannel0,ivec2(u/4.)+ivec2(-1,0),0).xyz);
    float mmin = min(min(m1,m2),min(m3,min(m4,m5)));

    o = vec4(0);
    vec4 O = vec4(0);
    int a = 3;
    for(int ix = 0;ix < a;ix++){
        for(int iy = 0;iy < a;iy++){
            O = vec4(0);if(mmin != 0.)mn(O,u+vec2(ix,iy)/float(a),mmin);o += O;
        }
    }
    o /= float(a*a);
}

mat2 rotate(float r){return mat2(cos(r),sin(r),-sin(r),cos(r));}
vec3 rotate(vec3 c,float r){return vec3(rotate(r)*c.xz,c.y).xzy;}
float de(vec3 c){
    return min(c.y+1.,length(c-vec3(0,0,3))-.3);
}
vec4 doMarch(vec3 pos,vec3 cam){
    int i = 0;
    vec3 march = vec3(0);
    float d = 1.;

    for(;i<200&&d>0.1&&length(march)<50.;i++){
        d = de(pos+march);
        march += d*cam;
    }
    return vec4(march+pos,i);
}
void mainImage(out vec4 o, in vec2 u){
    if(u.x*4.>iResolution.x||u.y*4.>iResolution.y)return;
    vec2 uv = (u*4.-iResolution.xy/2.)/iResolution.y;

    vec3 pos = vec3(0);
    vec2 look = vec2(0);
    vec3 cam = rotate(rotate(normalize(vec3(uv,1)).yxz,-look.y).yxz,look.x);

    vec4 march = doMarch(pos,cam);

    o = vec4(march);
}
