//fork of https://www.shadertoy.com/view/3l23Rc
// (wave visualiser)
// (use mouse to scale and offset)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float scale = ceil(max((iMouse.y / iResolution.y) * 4., 1.)) / 4.;
    scale = pow(scale, 2.);
    float offset = iMouse.x / iResolution.x;
  	vec2 t = wave(uv.x * scale + offset + iTime) * .5 + .5;

    vec2 l = vec2(uv.y) - t;
    l /= fwidth(l);
    l = abs(l);

    vec3 col = vec3(l.x, l.y, l.x);

    fragColor = vec4(col,1.0);
}

//fork of https://www.shadertoy.com/view/3l23Rc
// https://www.shadertoy.com/view/WlSGWw

#define a 2.05
#define b 3.
#define c 4.

vec2 kick(float time, float bps) { // https://www.shadertoy.com/view/3l23Rc

    // loop time
    float tm = mod(time, 1./bps);

    // adjusts frequency
    float finetune = 1.8;

    // KICK

    // sine that drops to 0 freq
	float k = sin(80. * exp(-tm * finetune * 10.));
    //return vec2(k) * .5;

	// ramp up start, fixes glitch
    k *= min(1., tm * 500.) * max(0., 1. - tm);
    //return vec2(k) * .5;

    // fade out the end
    k *= exp(-tm * 10.);
    //return vec2(k) * .5;

    // add a little more bass complexity 
    k *= cos(120.0 * exp(-tm * 2.));
	//return vec2(k) * .5;

    // SUB

    // low freq
	float s = sin(tm * 380.);
    //return vec2(s) * .5;

    // fade out
    s *= exp(-tm * 1.5);
    //return vec2(s) * .5;

    // ramp up start (fixes glitch) and fade to 0
    s *= min(1., tm * 100.) * max(0., .5 - tm);
    //return vec2(s) * .5;

    // add kick
    s += k;
    //return vec2(s) * .5;

    // incrase volume at the end
	s *= mix(1., 7., tm * 2.);
	//return vec2(s) * .5;

    // add more kick
    return vec2(s + k) * .5;
}

vec2 wave(float time) {
    time *= 1.;
    return kick(time, a) + kick(time, b) + kick(time, c);
}

vec2 mainSound( in int samp, float time )
{
    return wave(time);
}
