mat2 Rot(float a) {
	float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float aa = 1. / iResolution.y;
    vec3 col = vec3(.12);
    float t = iTime*3.;

    vec3 red1 = vec3(1., 0.4, 0.1);
    vec3 red2 = vec3(1., 0.6, 0.2);
    vec3 red = mix(red1, red2, sin(t*.1+cos(t*.25))*.5+.5);

    float d = length(uv);
    float c1aura = smoothstep(0.1, .0, d);
    red += mix(vec3(0.), vec3(1., .7, .2), c1aura * .4);

    float c1 = smoothstep(0.1+aa, 0.1, d);
    col = mix(col, red, c1);

    float ta = sin(iTime*1.5)*.5+1.5;
    float ax = .3 * ta;
    float ay = .09 * ta;
    vec2 satellitePos = vec2(uv.x-ax*sin(t), uv.y+ay*cos(t));
    d = length(satellitePos);
    float c2 = smoothstep(0.04+aa, 0.04, d);

    float distance = 1.;
    vec3 orange1 = vec3(1., .5, 0.);
    vec3 orange2 = vec3(1., .8, .4);
    vec3 orange = mix(orange1, orange2, distance);

    col = mix(col, orange, c2);

    if (uv.y +.1*cos(t) < 0.) col = mix(col, red, c1);

    /*ta = sin(iTime*1.9+20.)*.5+1.5;
    ax = .1 * ta;
    ay = .2 * ta;
    satellitePos = vec2(uv.x-ax*sin(t), uv.y+ay*cos(t));
    d = length(satellitePos);
    float c3 = smoothstep(0.04+aa, 0.04, d);

    vec3 blue = vec3(.2, .3, .8);
    col = mix(col, blue, c3);*/

    //if (uv.x-ax*sin(t) < 0.) col = mix(col, red, c1);

    fragColor = vec4(col,1.0);
}
